SELECT a.board_id, title, importance, DATE_FORMAT(start_date , '%Y/%m/%d') start_date, DATE_FORMAT(end_date , '%Y/%m/%d') end_date, a.code, ifnull(code_name , project ) project , DATEDIFF(now() , due_date) dday FROM boards a , codet b where 1=1 and a.code = b.code COLLATE utf8_unicode_ci
and board_id = #{code1}
and a.code = #{code}


SELECT distinct info_id, board_id, phone,  DATE_FORMAT(info_date  , '%Y%m%d')
FROM info
where board_id = #{boardId}
and phone = #{phone}
and DATE_FORMAT(info_date  , '%Y/%m/%d')= #{date}

mapper.delete:
DELETE FROM  info
	where board_id = #{boardId}
	and phone = #{phone}
	and DATE_FORMAT(info_date , '%Y/%m/%d') = #{date}

